$(document).ready(function() {
    var lang = "ukr";
    $(".lang").each(function(index, element) {
      $(this).text(languages[lang][$(this).attr("key")]);
    });
  });
  
  // get/set the selected language
  $(".translate").click(function() {
    var lang = $(this).attr("id");
  
    $(".lang").each(function(index, element) {
      $(this).text(languages[lang][$(this).attr("key")]);
    });
  });